INSERT INTO `UPTypeField` VALUES (260,'jump_rd_len_cutoff','Read Length Cutoff','integer','library_2',NULL,0,0,0,0,0,NULL,0,0,0,4,'100',NULL,NULL,'eval','!(isNaN(\"$_(val)\")) && parseInt(\"$_(val)\")>0','Please give a positive number greater than 0.','The assembler will cut the reads from the current library to this length.');
INSERT INTO `UPTypeField` VALUES (260,'jump_reverse_seq','Reverse Sequences','bool','library_2',NULL,0,0,0,0,0,NULL,0,0,0,2,'true',NULL,NULL,NULL,NULL,NULL,'This option takes value 0 or 1. It tells the assembler if the read sequences need to be complementarily reversed. Illumima GA produces two types of paired-end libraries: a) forward-reverse, generated from fragmented DNA ends with typical insert size less than 500 bp; b) reverse-forward, generated from circularizing libraries with typical insert size greater than 2 Kb. The parameter \"reverse_seq\" should be set to indicate this: 0, forward-reverse; 1, reverse-forward.');
